* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

body {
    font-family: 'Roboto', sans-serif;
    background-color: #f4f4f4;
    color: #333;
}

/* Hero Section */
.hero {
    position: relative;
    width: 100%;
    height: 500px;
    overflow: hidden;
}

.hero-img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.hero-text {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    color: white;
    text-align: center;
    padding: 0 20px;
}

.hero-text h1 {
    font-size: 3rem;
    margin-bottom: 20px;
    text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.7);
}

.hero-text p {
    font-size: 1.3rem;
    margin-bottom: 20px;
    text-shadow: 2px 2px 5px rgba(0, 0, 0, 0.7);
}

.hero-text .btn {
    background-color: #3498db;
    color: white;
    padding: 15px 30px;
    text-decoration: none;
    border-radius: 5px;
    font-size: 1.2rem;
    transition: background-color 0.3s;
}

.hero-text .btn:hover {
    background-color: #2980b9;
}

/* About Section */
.about {
    padding: 60px 0;
    background-color: #ffffff;
    text-align: center;
}

.about h2 {
    font-size: 2.5rem;
    margin-bottom: 20px;
    color: #34495e;
}

.about p {
    font-size: 1.1rem;
    line-height: 1.8;
    color: #555;
    max-width: 800px;
    margin: 0 auto;
}

/* Services Section */
.services {
    padding: 60px 0;
    background-color: #ecf0f1;
    text-align: center;
}

.service-cards {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 20px;
}

.service-card {
    width: 30%;
    padding: 30px;
    background-color: white;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
    transition: transform 0.3s ease-in-out;
}

.service-card:hover {
    transform: scale(1.05);
}

.service-card h3 {
    font-size: 1.8rem;
    color: #3498db;
    margin-bottom: 15px;
}

.service-card p {
    font-size: 1rem;
    color: #555;
}

/* Mobile Responsiveness for Services */
@media (max-width: 768px) {
    .service-card {
        width: 100%;
    }
}

/* Timing Section */
.timing {
    background-color: #ffffff;
    padding: 60px 0;
    text-align: center;
}

.timing h2 {
    font-size: 2.5rem;
    margin-bottom: 20px;
}

/* Reviews Section */
.reviews {
    padding: 60px 0;
    background-color: #ecf0f1;
    text-align: center;
}

.review-slider {
    display: flex;
    overflow: hidden;
}

.review-item {
    min-width: 300px;
    margin: 0 10px;
    padding: 20px;
    background-color: white;
    border-radius: 10px;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
    font-style: italic;
    color: #555;
}

/* Contact Section */
.contact {
    background-color: #ffffff;
    padding: 60px 0;
    text-align: center;
}

.contact h2 {
    font-size: 2.5rem;
    margin-bottom: 20px;
}

.contact p {
    font-size: 1.1rem;
    margin-bottom: 10px;
    color: #555;
}

footer {
    background-color: #2c3e50;
    color: white;
    padding: 20px 0;
    text-align: center;
}

footer a {
    color: #3498db;
    text-decoration: none;
}

footer a:hover {
    text-decoration: underline;
}

/* Animation for Reviews */
@keyframes slide {
    0% {
        transform: translateX(0);
    }
    100% {
        transform: translateX(-100%);
    }
}
